def task1(input):

    floor = 0

    for char in input:
        if char == "(":
            floor += 1
        else:
            floor -= 1

    return floor

def task2(input):
    
    floor = 0

    for i, char in enumerate(input):
        if char == "(":
            floor += 1
        else:
            floor -= 1

        if floor < 0:
            return i + 1
        
